      "from": {
        "key_code": "5",
        "modifiers": { "mandatory": ["right_option"] }
      },
      "to": [{ "shell_command": "osascript -e 'tell application \"System Events\" to keystroke \"Would like to bring my husband.\"'" }],
      "type": "basic"
    },
    {
      "from": {
        "key_code": "6",
        "modifiers": { "mandatory": ["right_option"] }
      },
      "to": [{ "shell_command": "osascript -e 'tell application \"System Events\" to keystroke \"My husbands name is Miguel Hernandez.\"'" }],
      "type": "basic"
